function getSteemMarket(){setTimeout(getSteemMarket,1e4),steem.api.getOrderBook(1,function(e,t){var r=parseFloat(t.asks[0].real_price).toFixed(6);$("#steemit_buy").text(r);var i=parseFloat(t.bids[0].real_price).toFixed(6);$("#steemit_sell").text(i)})}function getBlocktrades(){setTimeout(getBlocktrades,3e4),$.getJSON("price.php",{market:"blocktrades",action:"buy"},function(e,t){var r=parseFloat(e.price).toFixed(6);$("#blocktrades_buy").text(r)}),$.getJSON("price.php",{market:"blocktrades",action:"sell"},function(e,t){var r=parseFloat(e.price).toFixed(6);$("#blocktrades_sell").text(r)})}function getBittrex(){setTimeout(getBittrex,3e4),$.getJSON("price.php",{market:"bittrex",action:"buy"},function(e,t){var r=parseFloat(e.price).toFixed(6);$("#bittrex_buy").text(r)}),$.getJSON("price.php",{market:"bittrex",action:"sell"},function(e,t){var r=parseFloat(e.price).toFixed(6);$("#bittrex_sell").text(r)})}$(document).ready(function(){getSteemMarket(),getBlocktrades(),getBittrex()});